import becker.robots.*;public class StepRefineBot extends Robot{      public StepRefineBot(City aCity, int aStreet, int anAvenue, Direction aDir)   {  super(aCity, aStreet, anAvenue, aDir);   }      public void doit()   {  this.pickAll();      this.moveToStartOfPuts();      this.putAll();   }      private void pickAll()   {  this.pick4();      this.turnRight();      this.pick4();      this.turnLeft();      this.pick4();      this.turnRight();      this.pick4();      this.turnLeft();   }      private void pick4()   {  this.pickMove();      this.pickMove();      this.pickMove();      this.pickThing();   }      private void pickMove()   {  this.pickThing();      this.move();   }      private void turnRight()   {  this.turnLeft();      this.turnLeft();      this.turnLeft();   }      private void putAll()   {  this.put4();      this.turnRight();      this.put4();      this.turnLeft();      this.put4();      this.turnRight();      this.put4();   }      private void moveToStartOfPuts()   {  this.move3();      this.turnLeft();      this.move3();      this.turnLeft();   }      private void move3()   {  this.move();      this.move();      this.move();   }      private void put4()   {  this.putMove();      this.putMove();      this.putMove();      this.putThing();   }      private void putMove()   {  this.putThing();      this.move();   }}